INSERT INTO `article` VALUES (1,37,'22222222222222222','','font-weight:bold;','/uploads/20180321\\9c197a79866f0a4f31bd7c747691d6f2.png','2222222222','222222222222','<p>222222222222222</p>',1,1521610397,0,''),(3,6,'sdfsd','','','/public/themes/Home/Picture/2017-10-12/59defb91a8ef9.png','','','',0,1521610397,0,''),(4,6,'sdf','','','/public/themes/Home/Picture/2017-10-12/59defb91a8ef9.png','','','',0,1521610397,0,''),(5,6,'北京市大兴区人民政府与北京市国有','','','/public/themes/Home/Picture/2017-08-11/598d396942320.jpg','8月14日，中关村国家自主创新示范区国家新媒体产业基地授牌仪式在国家新媒体产业','北京市大兴区人民政府与北京市国有','',0,1521610397,0,''),(6,6,'新京报：文化都汇开盘热销3.6亿','','','/public/themes/Home/Picture/2017-08-10/598c2bf6ecc14.jpg','新京报讯 （记者李捷）6月26日，位于大兴区国家新媒体基地、紧邻南五环的文化都汇首次开盘，开发商表示，开盘当天劲揽','','',0,1521610397,0,''),(7,6,'文化都汇——中国首家物联网国家标准','','','/public/themes/Home/Picture/2017-08-18/59965cff6a304.jpg','2016年7月11日，云集全球物联网技术与标准领域最顶尖级专家的“2016 国际开放','','',0,1521610397,0,''),(8,6,'北京市文化置业有限公司荣获“2017京','','','/public/themes/Home/Picture/2017-10-12/59defb91a8ef9.png','北京市文化置业有限公司荣获“2017京津冀影响力品牌”','','',0,1521610397,0,''),(9,40,'文化都汇','','','/uploads/20180326\\17a26ba7391d0968f1fbda102cacbc7c.jpg','','','<p><span style=\"line-height:1.5\"><span style=\"line-height:2.5;font-size:16px\">文化都汇是北京市文化置业有限公司重点打造的北京首席文创产业综合体，矗立于北京大兴国家新媒体产业基地，总建筑面积约</span></span><span style=\"line-height:2.5;font-size:16px\">14</span><span style=\"line-height:2.5;font-size:16px\">万㎡。项目集多功能智能办公、多业态商业、小面积办公、高端商务功能为一体，由</span><span style=\"line-height:2.5;font-size:16px\">5</span><span style=\"line-height:2.5;font-size:16px\">栋独立办公楼构成，</span><span style=\"line-height:2.5;font-size:16px\">3.8</span><span style=\"line-height:2.5;font-size:16px\">米层高，</span><span style=\"line-height:2.5;font-size:16px\">110-2800</span><span style=\"line-height:2.5;font-size:16px\">平鲜氧办公空间，自由组合，业态互动，可随需定制，独栋冠名，全面满足多元型企业办公。项目地处首都第二机场的空港经济圈，紧邻城市五环和京开高速，交通便利。所在的大兴区国家</span><a></a><span style=\"line-height:2.5;font-size:16px\">新媒体产业基地区域为政府重点投资区域，有着多项经济实惠的产业扶持政策。</span> &nbsp;</p><p style=\"text-align:left;text-indent:43px\"><span style=\"line-height:2.5;font-size:16px\">文化都汇以其超前的规划设计、完善的商务配套、强有力的扶持政策，已经成为全国文创企业高度关注的置业首选。</span> &nbsp;</p><p style=\"text-indent:43px\"><br/> </p><p style=\"text-indent:43px\"><img src=\"http://www.bjwhzy.com.cn/Uploads/Editor/2017-08-23/599d1c57e8f6c.jpg\" alt=\"\"/> &nbsp;</p><p><br/></p>',1,1522051075,1,''),(10,40,'北京文化产权交易中心','','','/uploads/20180326\\5d144bbad012a8f86bb36b29e0b2a88e.jpg','','','<p style=\"text-indent:40px\"><span style=\"font-size:16px;line-height:2.5\">2015年</span><span style=\"font-size:16px;line-height:2.5\">5</span><span style=\"font-size:16px;line-height:2.5\">月，根据市文资办和东城区政府《关于促进首都文化要素市场建设全面战略合作协议》，双方以“促进首都文化中心功能建设”为宗旨，合作建设北京文化产权交易中心。该项目总投资</span><span style=\"font-size:16px;line-height:2.5\">16</span><span style=\"font-size:16px;line-height:2.5\">亿元，由文化置业公司按照政府引导，市场化方式运作推进建设与运营，建成后将成为东城区的文化地标。</span> &nbsp;</p><p style=\"text-indent:40px\"><span style=\"font-size:16px;line-height:2.5\">项目地处东城区前门地区，东至前门东路，西至布巷子胡同，南至京泰龙国际酒店，北至阳平会馆南路。总占地面积</span><span style=\"font-size:16px;line-height:2.5\">0.82</span><span style=\"font-size:16px;line-height:2.5\">万平米，建筑面积共计</span><span style=\"font-size:16px;line-height:2.5\">6.10</span><span style=\"font-size:16px;line-height:2.5\">万平方米。项目致力于打造文化产权交易平台、文化产业“互联网</span><span style=\"font-size:16px;line-height:2.5\">+</span><span style=\"font-size:16px;line-height:2.5\">”投融资平台、文化企业孵化平台、文化产权登记托管保护平台、文化产权信息发布平台五大平台。</span> &nbsp;</p><p><br/></p>',1,1522051149,0,''),(11,40,'宋庄艺术小镇','','','/uploads/20180326\\1e9e371fa04b83191b7d9f71cbdba781.jpg','','','<p style=\"text-indent:40px\"><span style=\"line-height:2.5;font-size:16px\">2015年</span><span style=\"line-height:2.5;font-size:16px\">3</span><span style=\"line-height:2.5;font-size:16px\">月，根据文资办及文投集团的指示，文化置业公司通过市场竞拍方式，以</span><span style=\"line-height:2.5;font-size:16px\">10.8</span><span style=\"line-height:2.5;font-size:16px\">亿元获取宋庄</span><span style=\"line-height:2.5;font-size:16px\">A1</span><span style=\"line-height:2.5;font-size:16px\">地块，项目是按照政府引导和市场化运作原则，结合通州区政府提出的“建设世界文化名镇，打造中国文化硅谷，争创国家文化自主创新示范区”的战略构想，打造国际化的艺术聚集区。宋庄</span><span style=\"line-height:2.5;font-size:16px\">A1</span><span style=\"line-height:2.5;font-size:16px\">地块土地出让年限为商业</span><span style=\"line-height:2.5;font-size:16px\">40</span><span style=\"line-height:2.5;font-size:16px\">年、办公</span><span style=\"line-height:2.5;font-size:16px\">50</span><span style=\"line-height:2.5;font-size:16px\">年，规划总用地面积</span><span style=\"line-height:2.5;font-size:16px\">13.70</span><span style=\"line-height:2.5;font-size:16px\">万平方米，建设用地面积</span><span style=\"line-height:2.5;font-size:16px\">6.59</span><span style=\"line-height:2.5;font-size:16px\">万平方米，地上建筑面积</span><span style=\"line-height:2.5;font-size:16px\">16.47</span><span style=\"line-height:2.5;font-size:16px\">万平方米。</span> &nbsp;</p><p style=\"text-indent:40px\"><br/> </p><p><span style=\"line-height:2.5;font-size:16px\">项目地处通州区宋庄镇，紧邻宋庄文化聚集区中心街，周边艺术人才集聚，区位优势凸显。京哈高速、六环路两条交通线与项目相邻，距朝阳区</span><span style=\"line-height:2.5;font-size:16px\">CBD</span><span style=\"line-height:2.5;font-size:16px\">仅需</span><span style=\"line-height:2.5;font-size:16px\">30</span><span style=\"line-height:2.5;font-size:16px\">分钟车程，与首都国际机场距离</span><span style=\"line-height:2.5;font-size:16px\">13</span><span style=\"line-height:2.5;font-size:16px\">公里，辐射“首都城市圈”和“京津冀都市圈”。</span></p><p><br/></p>',1,1522051201,0,''),(12,43,'444444444444','','','','','','<p>4444444444<br/></p>',1,1522056571,0,'');
