INSERT INTO `category` VALUES (1,'新闻中心','news','',0,2,'article','0','1,5,6',0,'NEWS CENTER','最新动态','最新动态',1,0,1,0,'/public/themes/Home/images/tu33.jpg',1,'','article_list','article_show',14,'1,2,3',0,0),(2,'关于我们','about','',0,13,'page','0','2,33,34,35,36,37',0,'ABOUT US','CLTPHP内容管理系统，微信公众平台、APP移动应用设计、HTML5网站API定制开发。大型企业网站、个人博客论坛、手机网站定制开发。更高效、更快捷的进行定制开发。','CLTPHP内容管理系统，微信公众平台、APP移动应用设计、HTML5网站API定制开发。大型企业网站、个人博客论坛、手机网站定制开发。更高效、更快捷的进行定制开发。',0,0,1,0,'/public/themes/Home/Picture/2017-08-10/598c25402f788.jpg',0,'','article_list','article_show',1,'',0,0),(4,'采购平台','product','',0,2,'article','0','4,43,44,45',0,'purchasing platform','CLTPHP系统操作,CLTPHP,CLTPHP内容管理系统','CLTPHP系统操作,CLTPHP,CLTPHP内容管理系统',3,0,1,0,'/public/themes/Home/images/tu49.jpg',0,'','article_list','article_show',0,'1,2,3',0,0),(3,'主营业务','services','',0,2,'article','0','3,40,41,42',0,'product service','产品服务,CLTPHP,CLTPHP内容管理系统','产品服务',2,0,1,0,'/public/themes/Home/images/tu50.jpg',0,'','article_list','article_show',15,'1,2,3',0,0),(33,'组织架构','about','about/',2,13,'page','0,2','33',0,'asdfasdf','asdfasdf','asdfasf',2,0,1,0,'',0,'','page_show','page_show',0,'',0,0),(5,'集团新闻','news','news/',1,2,'article','0,1','5',0,'CLTPHP动态','CLTPHP动态','CLTPHP动态',1,0,1,0,'/public/themes/Home/images/tu33.jpg',0,'','article_list','article_show',5,'1,2,3',0,0),(6,'公司新闻','news','news/',1,2,'article','0,1','6',0,'CLTPHP相关知识1','CLTPHP相关知识','CLTPHP相关知识',0,0,1,0,'',0,'','article_list','article_show',14,'',0,0),(34,'公司介绍','about','about/',2,13,'page','0,2','34',0,'asdfasdf','asdfasdf','asdfasf',0,0,1,0,'',0,'','page_show','page_show',0,'',0,0),(35,'领导致辞','about','about/',2,13,'page','0,2','35',0,'asdfasdf','asdfasdfsdfs','asdfasf',1,0,1,0,'',0,'','page_show','page_show',0,'',0,0),(36,'企业文化','about','about/',2,13,'page','0,2','36',0,'asdfasdf','asdfasdf','asdfasfsdfsdf',3,0,1,0,'/public/themes/Home/Picture/2017-08-10/598c2580ea2a8.jpg',0,'','page_show','page_show',0,'',0,0),(37,'发展历程','about','about/',2,2,'article','0,2','37',0,'发展历程','发